import React, { useState, useRef } from 'react';
import { subirAudio } from '../services/api';
import ResultDisplay from './ResultDisplay';
import { obtenerTraduccion } from '../services/api';


const AudioRecorder = () => {
  const [recording, setRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
const handleFileChange = (e) => {
  const file = e.target.files[0];
  if (file) {
    setSelectedFile(file);
    setAudioBlob(file); // Lo tratamos igual que un blob grabado
  }
};

  const mediaRecorderRef = useRef(null);
  const audioChunks = useRef([]);
  const [resultado, setResultado] = useState(null);


  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    mediaRecorderRef.current = new MediaRecorder(stream);

    mediaRecorderRef.current.ondataavailable = (event) => {
      audioChunks.current.push(event.data);
    };

    mediaRecorderRef.current.onstop = () => {
      const audio = new Blob(audioChunks.current, { type: 'audio/wav' });
      setAudioBlob(audio);
      audioChunks.current = [];
    };

    mediaRecorderRef.current.start();
    setRecording(true);
  };

  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setRecording(false);
  };

    const handleSend = async () => {
    if (!audioBlob) return;
    const formData = new FormData();
    formData.append('audio', audioBlob, 'grabacion.wav');

    try {
        const res = await subirAudio(formData);
        const id = res.data.id;

        // Solicita la traducción al backend
        const respuesta = await obtenerTraduccion(id);
        setResultado(respuesta.data);
    } catch (error) {
        console.error(error);
        alert("Error al enviar o recibir datos del backend.");
    }
    };

  return (
    <div className="p-6 bg-white rounded-lg shadow-md max-w-md mx-auto text-center space-y-4">
      <h2 className="text-xl font-semibold">Grabador de voz</h2>
      <button
        onClick={recording ? stopRecording : startRecording}
        className={`px-4 py-2 rounded text-white ${
          recording ? 'bg-red-500' : 'bg-blue-500'
        }`}
      >
        {recording ? 'Detener' : 'Grabar'}
      </button>

      {audioBlob && (
        <>
          <audio controls src={URL.createObjectURL(audioBlob)} />
          <button
            onClick={handleSend}
            className="mt-2 bg-green-600 text-white px-4 py-2 rounded"
          >
            Enviar audio al backend
          </button>
        </>
      )}
      <ResultDisplay resultado={resultado} />
    </div>
  );
};

export default AudioRecorder;
