import React, { useEffect, useState } from 'react';
// import { obtenerHistorial, eliminarTraduccion } from '../services/api'; // Descomenta esto cuando tengas backend real

const History = () => {
  const [historial, setHistorial] = useState([]);

  useEffect(() => {
    // Simulación de datos falsos
    const historialFalso = [
      {
        id: 'abc123',
        texto: 'Hola, ¿cómo estás?',
        videoUrl: 'https://www.w3schools.com/html/mov_bbb.mp4',
        fecha: '2025-06-20',
      },
      {
        id: 'xyz789',
        texto: 'Gracias por su atención',
        videoUrl: 'https://www.w3schools.com/html/movie.mp4',
        fecha: '2025-06-19',
      },
    ];

    setHistorial(historialFalso);
    // Para backend real, usa: obtenerHistorial().then(res => setHistorial(res.data));
  }, []);

  const handleEliminar = async (id) => {
    const confirmar = window.confirm('¿Estás seguro que deseas eliminar esta traducción?');
    if (!confirmar) return;

    // Simulación: eliminar localmente
    setHistorial((prev) => prev.filter((item) => item.id !== id));

    // Para backend real:
    // try {
    //   await eliminarTraduccion(id);
    //   setHistorial((prev) => prev.filter((item) => item.id !== id));
    // } catch (error) {
    //   console.error('Error al eliminar:', error);
    //   alert('Error al eliminar la traducción.');
    // }
  };

  return (
    <div className="p-6 bg-white rounded-xl shadow-lg max-w-xl mx-auto">
    <h2 className="text-2xl font-bold text-center text-gray-800 mb-4">Historial de Traducciones</h2>
      {historial.length === 0 ? (
        <p className="text-gray-600 text-center">No hay traducciones aún.</p>
      ) : (
        historial.map((item) => (
<div key={item.id} className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50">
            <p><strong>Texto:</strong> {item.texto}</p>
            <p><strong>Fecha:</strong> {item.fecha}</p>
            {item.videoUrl && (
              <video controls width="300" className="mt-2 rounded">
                <source src={item.videoUrl} type="video/mp4" />
              </video>
            )}
            <button
              onClick={() => handleEliminar(item.id)}
              className="mt-2 text-sm bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700"
            >
              Eliminar
            </button>
          </div>
        ))
      )}
    </div>
  );
};

export default History;

